INSERT INTO `exp_channel_data` VALUES(12, 1, 1, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, 'Art and science have a relationship that is oft-at-odds, oft-at-ease.<em><a href="http://www.amazon.com/The-Where-Why-How-Illustrate/dp/1452108226">The Where, the Why, and the How</a></em> focuses on the latter. We’re stoked to be included in <a href="http://also-online.com/">Also Online’s</a> new book that pairs writers with illustrators in explaining the mysteries of the scientific world. From the God particle, to the theories on why we sleep and dream, this book is loaded with amazing stuff from some great image makers like <a href="http://jordinisip.com/">Jordan Isip</a>, <a href="http://www.joshcochran.net/">Josh Cochrane</a> and <a href="http://www.morningcraft.com/">Bettsy Walton</a>. While we can draw pretty pictures (sometimes), we don’t fancy ourselves book reviewers so we’ll point you on over to <a href="http://www.brainpickings.org/index.php/2012/10/12/where-why-how-julia-rothman/">BrainPickings</a> for their stellar write up.', 'xhtml', '{filedir_1}wherewhyhow_Blog_Narrow_Standard.jpg', 'none', '1', 'none', 'Art and science have a relationship that is oft-at-odds, oft-at-ease.<em><a href="http://www.amazon.com/The-Where-Why-How-Illustrate/dp/1452108226">The Where, the Why, and the How</a></em> focuses on the latter. We’re stoked to be included in Also Online’s new book that pairs writers with illustrators in explaining the mysteries of the scientific world. ', 'none', '{filedir_1}Gravity_Detail_Retina.jpg', 'none', '', NULL, '', NULL, '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none', '', 'none',