INSERT INTO `exp_channel_data` VALUES(316, 1, 2, '', 'none', '1', 'none', 'Nike', 'none', '', 'none', 'The Penn Relays are the longest and most storied collegiate track and field event in the country. Nike asked us to design and install a mural over the course of the three day event. Rather than design a standard mural that would deb applied with paint and projected we wanted to make a piece that would not only evolve over the course of the event but also reflect the style and grittiness of Philadelphia.', 'none', '', 'none', '', 'none', '2013', 'none', '', 'none', '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '1', 'none', '1', 'none', '1', 'none', '1', 'none', '1', 'none', '1', 'none', '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, 'Nike', 'none', '', 'none', '', 'none', '', NULL, '', NULL, '', NULL, '', NULL, '', 'none', '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', NULL, '', 'none');
