INSERT INTO `exp_revision_tracker` VALUES(118, 11, 'exp_templates', 'template_data', 1367456977, 1, '\n		<script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>\n		\n\n		<script src="/public/a/j/plugins.js"></script>\n		<script src="/public/a/j/vendor/jquery.royalslider.custom.min.js"></script>\n		<script src="/public/a/j/vendor/jquery.timeago.js"></script>\n		<script src="/public/a/j/main.js"></script>\n		<script src="/public/a/j/vendor/stickystack.js"></script>\n		<script src="/public/a/j/vendor/jquery.ddslick.min.js"></script>\n		<script src="/public/a/j/vendor/waypoints.min.js"></script>\n	\n\n\n\n		<script>\n		    jQuery(document).ready(function($) {\n		        $(".royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	arrowsNav: false,\n		        	autoHeight:true,\n		        	loop: true\n		        }); \n		        $(".royalSlider2").royalSlider({\n    				controlNavigation: ''none'',\n    				arrowsNav: false,\n    				autoHeight:true,\n    				loop: true\n\n    			});  	\n		        $(".cs-royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	arrowsNav: false,\n		        	autoScaleSlider:true,\n		        	loop: true, \n		        	autoScaleSliderWidth: ''1190'', \n		        	autoScaleSliderHeight: ''506'',\n		        	imageScalePadding: 0,\n		        	slideSpacing: 0\n		        }); \n	  		 var slider = $(".royalSlider").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider.prev();\n				})\n				$(''.next'').click(function(){\n					slider.next();\n\n				});\n			var slider2 = $(".royalSlider2").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider2.prev();\n				})\n				$(''.next'').click(function(){\n					slider2.next();\n\n				});	\n			var slider3 = $(".cs-royalSlider").data(''royalSlider'');\n				$(''.prev-cs'').click(function(){\n					slider3.prev();\n				})\n				$(''.next-cs'').click(function(){\n					slider3.next();\n\n				});					\n			});\n\n			$(''.styled-select'').ddslick();\n		\n\n			$container = $(".header-inner-wrapper")\n			$totalHeight = $container.outerHeight();\n			//$workHeader = $(".work-detail header.fixed.expanded");\n\n			$(''.content'')\n			  .waypoint(function(direction) {\n			    if (direction === ''down'') { \n			    	console.log(''waypoint down'')\n			    	$(".hover-trigger").fadeIn("fast");\n			    	$container.removeClass("top").addClass("fixed").css("top", -$totalHeight);\n			    	$("body").css("padding-top", $totalHeight);\n			    }\n			  }, { offset: 20 })\n			  .waypoint(function(direction) {\n			    if (direction === ''up'') { \n			    	console.log(''waypoint up'')\n			    	$(".hover-trigger").fadeOut("fast");\n			    	$container.removeClass("fixed").css("top", 0);\n			    	$("body").css("padding-top", 0);\n			    	$(".hover-trigger span").fadeIn();\n			    }\n			  }, { offset: 20 });\n\n	\n\n			$(''.hover-trigger'').hover(function() {\n				$(".hover-trigger span").fadeOut();\n				$(".header-inner-wrapper").addClass("expanded").animate({\n	  				top: 0\n				});\n\n			});\n\n			$container.mouseleave(function() {\n				$(this).removeClass("expanded").animate({\n					top:  -$totalHeight\n				});\n				$(".hover-trigger span").fadeIn();\n			});				\n\n			$container.waypoint(function() {\n				$(this).addClass("top")\n			});	\n				\n	</script>\n\n	<script>\n		$(document).ready(function(){\n			// Show search modal\n			$(''#search-modal-button'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeIn(500);\n				$(''#search-modal-keywords'').focus();\n			});\n\n			// Close search modal\n			$(''#search-modal-close'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeOut(100);\n			});\n\n			// Catch the search modal submit event\n			// and do the searches via ajax\n			$(''#search-modal-form'').submit(function(e){\n				e.preventDefault();\n\n				// grab the keywords field\n				var keywords = $(''#search-modal-keywords'').val();\n				\n				// clear any existing results\n				$(''.search-results .wrap'').html('''');\n\n				// set the two hidden keywords field\n				// values to the user input\n				$(''#blog-keywords'').val(keywords);\n				$(''#work-keywords'').val(keywords);\n\n				// perform the ajax post for the blog search\n				var action = $(''#blog-search-form'').attr(''action'');\n				var form = $(''#blog-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n				// perform ajax post for the work search\n				var action = $(''#work-search-form'').attr(''action'');\n				var form = $(''#work-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n			});\n			\n		});\n	</script>');
INSERT INTO `exp_revision_tracker` VALUES(119, 36, 'exp_templates', 'template_data', 1367456977, 1, '\n		{embed="inc/head"}\n	<body class="search-results-page">\n		<!--[if lt IE 7]>\n			<p class="chromeframe">You are using an <strong>outdated</strong> browser. Please <a href="http://browsehappy.com/">upgrade your browser</a> or <a href="http://www.google.com/chromeframe/?redirect=true">activate Google Chrome Frame</a> to improve your experience.</p>\n		<![endif]-->\n		<style>\n			.search-results-page  .search-results { position: static; }\n		</style>\n		{embed="inc/header"}\n		\n			<div class="search-drawer">\n				<div class="topper">\n					<div class="wrap">\n						<form id="search-results-form">\n							<input type="text" id="search-results-keywords" value="{exp:search:keywords}" />\n						</form>\n						{!--\n						<!--<a class="close ss-icon ss-pika" href="#">&#x2421;</a>-->\n						<a class="close" href="#">x</a>\n						--}\n					</div>\n				</div>\n			</div><!-- /search-drawer-->\n			<div class="search-results">\n				<div class="wrap">\n				{exp:search:search_results}\n				{if count==1}\n				\n					<div class="adjunct-header">\n						<p class="results-count"><strong>{exp:search:total_results} results in <span>{channel_name}</span></strong></p>\n						\n					</div>\n\n					<ul>\n				{/if}		\n						<li>\n							<a href="{auto_path}">\n								{if channel_short_name == "blog"}\n									<img src="{home_feature_image}" />\n								{if:elseif channel_short_name == "work"}\n									<img src="{archive_thumb}" />\n								{/if}\n								<div>\n									\n									<p class="meta">\n							Category | <em>{categories backspace="2"}{category_name}, {/categories}</em</p>\n									<h3>{title}</h3>\n									<p>{excerpt}</p>\n								</div>\n							</a>\n						</li>\n				{if count == total_results}		\n					</ul>\n				{/if}\n\n\n				{paginate}\n				<div class="search-pagination">\n        			<p>Page {current_page} of {total_pages} pages {pagination_links}</p>\n    			\n				</div>\n				{/paginate}	\n				{/exp:search:search_results}	\n				</div>\n			</div><!-- /search-results-->\n			\n\n\n\n\n\n\n\n		{embed="inc/footer"}\n		{embed="inc/jsReference"}\n		<script>\n			$(document).ready(function(){\n				// Catch the search form submit event\n				// and do the searches via ajax\n				$(''#search-results-form'').submit(function(e){\n					e.preventDefault();\n\n					// grab the keywords field\n					var keywords = $(''#search-results-keywords'').val();\n					\n					// clear any existing results\n					$(''.search-results .wrap'').html('''');\n\n					// set the two hidden keywords field\n					// values to the user input\n					$(''#blog-keywords'').val(keywords);\n					$(''#work-keywords'').val(keywords);\n\n					// perform the ajax post for the blog search\n					var action = $(''#blog-search-form'').attr(''action'');\n					var form = $(''#blog-search-form'').serialize();\n\n					$.post(action, form, function(data){\n						$(''.search-results .wrap'').append(data);\n					});\n\n					// perform ajax post for the work search\n					var action = $(''#work-search-form'').attr(''action'');\n					var form = $(''#work-search-form'').serialize();\n\n					$.post(action, form, function(data){\n						$(''.search-results .wrap'').append(data);\n					});\n\n				});\n			});\n		</script>\n		{!-- these are the search forms created by ee. we serialize them with jquery and submit with ajax --}\n		<div id="search-forms" style="display:none;">\n		{exp:search:simple_form \n			channel="blog" \n			form_id="blog-search-form"\n			result_page="inc/search-results-ajax-blog"\n			no_result_page="inc/search-no-results-ajax/blog"\n			results="10"\n			search_in="everywhere"\n		}\n		<input type="hidden" id="blog-keywords" name="keywords" value="" >  \n		{/exp:search:simple_form}\n\n		{exp:search:simple_form \n			channel="work" \n			form_id="work-search-form"\n			result_page="inc/search-results-ajax-work"\n			no_result_page="inc/search-no-results-ajax/work"\n			results="10"\n			searc