INSERT INTO `exp_revision_tracker` VALUES(293, 11, 'exp_templates', 'template_data', 1371083417, 1, '\n		<script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>\n		\n\n		<script src="/public/a/j/plugins.js"></script>\n		<script src="/public/a/j/vendor/jquery.royalslider.custom.min.js"></script>\n		<script src="/public/a/j/vendor/jquery.timeago.js"></script>\n		<script src="/public/a/j/main.js"></script>\n		<script src="/public/a/j/vendor/stickystack.js"></script>\n		<script src="/public/a/j/vendor/jquery.ddslick.min.js"></script>\n		<script src="/public/a/j/vendor/waypoints.min.js"></script>\n	\n\n\n\n		<script>\n		    jQuery(document).ready(function($) {\n		        $(".royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	loop: true,\n		        	arrowsNav: false,\n		        	autoHeight:true\n\n		        }); \n		        $(".royalSlider2").royalSlider({\n    				controlNavigation: ''none'',\n    				arrowsNav: false,\n    				autoHeight:true,\n    				loop: true\n\n    			});  	\n		        $(".cs-royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	arrowsNav: false,\n		        	autoScaleSlider:true,\n		        	loop: true, \n		        	autoScaleSliderWidth: ''1190'', \n		        	autoScaleSliderHeight: ''506'',\n		        	imageScalePadding: 0,\n		        	slideSpacing: 0\n		        }); \n	  		 var slider = $(".royalSlider").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider.prev();\n				})\n				$(''.next'').click(function(){\n					slider.next();\n\n				});\n			var slider2 = $(".royalSlider2").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider2.prev();\n				})\n				$(''.next'').click(function(){\n					slider2.next();\n\n				});	\n			var slider3 = $(".cs-royalSlider").data(''royalSlider'');\n				$(''.prev-cs'').click(function(){\n					slider3.prev();\n				})\n				$(''.next-cs'').click(function(){\n					slider3.next();\n\n				});					\n			});\n\n			$(''.styled-select'').ddslick();\n		</script>\n		<script>\n\n			$container = $(".header-inner-wrapper")\n			$totalHeight = $container.outerHeight();\n			//$workHeader = $(".work-detail header.fixed.expanded");\n\n			$(''.content'')\n			  .waypoint(function(direction) {\n			    if (direction === ''down'') { \n			    	console.log(''waypoint down'')\n			    	$(".hover-trigger").fadeIn("fast");\n			    	$container.removeClass("top").addClass("fixed").css("top", -$totalHeight);\n			    	$("body").css("padding-top", $totalHeight);\n			    }\n			  }, { offset: 20 })\n			  .waypoint(function(direction) {\n			    if (direction === ''up'') { \n			    	console.log(''waypoint up'')\n			    	$(".hover-trigger").fadeOut("fast");\n			    	$container.removeClass("fixed").css("top", 0);\n			    	$("body").css("padding-top", 0);\n			    	$(".hover-trigger span").fadeIn();\n			    }\n			  }, { offset: 20 });\n\n	\n\n			$(''.hover-trigger'').hover(function() {\n				console.log("running");\n				$(".hover-trigger span").fadeOut();\n				$(".header-inner-wrapper").addClass("expanded").animate({\n	  				top: 0\n				});\n\n			});\n\n			$container.mouseleave(function() {\n				$(this).removeClass("expanded").animate({\n					top:  -$totalHeight\n				});\n				$(".hover-trigger span").fadeIn();\n			});				\n\n			$container.waypoint(function() {\n				$(this).addClass("top")\n			});	\n				\n	</script>\n\n	<script>\n		$(document).ready(function(){\n			// Show search modal\n			$(''#search-modal-button'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeIn(500);\n				$(''#search-modal-keywords'').focus();\n			});\n\n			// Close search modal\n			$(''#search-modal-close'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeOut(100);\n			});\n\n			// Catch the search modal submit event\n			// and do the searches via ajax\n			$(''#search-modal-form'').submit(function(e){\n				e.preventDefault();\n\n				// grab the keywords field\n				var keywords = $(''#search-modal-keywords'').val();\n				\n				// clear any existing results\n				$(''.search-results .wrap'').html('''');\n\n				// set the two hidden keywords field\n				// values to the user input\n				$(''#blog-keywords'').val(keywords);\n				$(''#work-keywords'').val(keywords);\n\n				// perform the ajax post for the blog search\n				var action = $(''#blog-search-form'').attr(''action'');\n				var form = $(''#blog-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n				// perform ajax post for the work search\n				var action = $(''#work-search-form'').attr(''action'');\n				var form = $(''#work-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n			});\n\n			// Load archives\n			$(''.see-more h2 a'').click(function(e){\n				e.preventDefault();\n				\n				var page = $(''nav.subnav ul li.on a'').data(''page'');\n\n				var category = $(''section#'' + page + '' input.category'').val();\n				var children = $(''section#'' + page + '' input.children'').val();\n				children.replace(''|'', ''&'');\n\n				$.get(''/inc/archives/'' + category + ''/'' + children, function(data) {\n  					$(''#archive-holder'').html(data);\n  				});\n				\n			});\n\n			$(document).on(''click'',''.subnav a'',function(e){\n				\n 				if  ($(''nav.subnav ul li.on a'').data(''page'') == "branding" || $(''nav.subnav ul li.on a'').data(''page'') == "books" || $(''nav.subnav ul li.on a'').data(''page'') == "ads-collateral") {\n 					$(".see-more").hide();\n 				}\n 				else {\n 					$(".see-more").show();\n\n 				}\n\n			});\n\n			\n		});\n	</script>');
