INSERT INTO `exp_revision_tracker` VALUES(318, 10, 'exp_templates', 'template_data', 1371087267, 1, '<footer id="contact">\n	<div class="wrap">\n\n		<div class="col-1">\n			<h2>Contact Us</h2>\n\n			<div class="split2">\n\n				<div class="col first">\n\n					<p class="address">\n						<strong>The Heads of State</strong>\n						<a target="_blank"  title="Go to Google Maps" href="https://maps.google.com/maps?q=39+N.+3rd+Street+%7C+2nd+Floor&amp;ie=UTF-8&amp;hq=&amp;hnear=0x89c6c885cdfed2f9:0x7f7f7b0de6fc04cb,39+N+3rd+St,+Philadelphia,+PA+19106&amp;gl=us&amp;daddr=39+N+3rd+St,+Philadelphia,+PA+19106&amp;ei=ZKWmUcLyDtjI4APrlYHAAQ&amp;ved=0CDIQwwUwAA">39 N. 3rd Street | 2nd Floor<br />\n						Philadelphia, PA 19106</a>\n					</p>\n\n				</div>\n\n				<div class="col social-header">\n\n					<h3>Follow Us</h3>\n					<p>\n						<a class="f-fb ss-social ss-icon" target="_blank" href="https://www.facebook.com/theheadsofstate">Facebook</a>\n						<a class="f-twitter ss-social ss-icon" target="_blank"  href="https://twitter.com/theheadsofstate">Twitter</a>\n						<a class="f-instagram ss-social ss-icon" target="_blank"  href="http://instagram.com/theheadsofstate/">Instagram</a>\n						<a class="f-pintrest ss-social ss-icon" target="_blank"  href="http://pinterest.com/theheadsofstate/">Pinterest</a>\n					</p>\n\n				</div><!--col-->\n\n			</div><!-- split-2-alt -->\n\n			<p class="f-email ss-standard ss-mail"><a href="mailto:studio@theheadsofstate.com">studio@theheadsofstate.com</a></p>\n\n			<form class="mailing-list">\n				<label class="move">Join Our Mailing List</label>\n				<input type="text" placeholder="Join our mailing list">\n				<input type="submit" class="btn" value="Sign Up">\n			</form>\n\n		</div><!-- col-->\n\n		<div class="col-2">\n\n			<h2>Questions?</h2>\n\n			{exp:freeform:form \n				form:class="split2"\n				form_name="contact"\n				return="site/contact-confirmation" \n			}\n				<div class="col first">\n					<input type="text" placeholder="Name" name="name"/>\n					<input type="email" placeholder="Email" name="email"/>\n					<input type="tel" placeholder="Phone" name="phone"/>\n					<input type="text" placeholder="Company" name="company"/>\n				</div>\n				<div class="col">\n					<select class="styled-select" style="width: 100%" name="business">\n						<option>How May We Direct Your Call?</option>\n						<option value="New Business">New Business</option>\n						<option value="Old Business">Old Business</option>\n						<option value="Love Letters">Love Letters</option>\n						<option value="Press and Speaking">Press &amp; Speaking</option>\n						<option value="General Questions">General Questions</option>\n					</select>\n\n					<textarea name="message"></textarea>\n\n					<input type="submit" class="btn" value="Submit">\n				</div>\n			{/exp:freeform:form}\n		</div><!-- col -->\n\n\n\n		<p class="copyright">&copy; The Heads of State 2013. All rights Reserved.</p>\n\n	</div>\n</footer>');
