INSERT INTO `exp_revision_tracker` VALUES(332, 9, 'exp_templates', 'template_data', 1371087267, 1, '<section class="social-footer cards">\n\n	<span class="prev pager ss-icon ss-standard ss-navigaterright">Previous</span>\n	<span class="next pager ss-icon ss-standard ss-navigaterleft">Next</span>\n\n	<div class="wrap">\n\n		<div class="adjunct-header">\n			<ul>\n				<li><a class="on blog-trigger" href="#">Blog</a></li>\n				<li><a class="insta-trigger" href="#">Instagram</a></li>\n			</ul>\n			<p class="adjunct-link"><a href="/site/blog/">See All Posts</a></p>\n		</div>\n\n\n		<h2 class="move">Blog</h2>\n		<div class="royalSlider rsDefault blog-panel">\n			{exp:channel:entries channel="blog" dynamic="no" limit="15"}\n			{if count == ''1''}<div class="split3 handheld-horz rsContent">{/if}\n			{if count == ''4''}<div class="split3 handheld-horz rsContent">{/if}\n			{if count == ''7''}<div class="split3 handheld-horz rsContent">{/if}\n			{if count == ''10''}<div class="split3 handheld-horz rsContent">{/if}\n			{if count == ''13''}<div class="split3 handheld-horz rsContent">{/if}\n\n			<article class="col {if count == "1"}first{/if}{if count == "4"}first{/if}{if count == "7"}first{/if}{if count == "10"}first{/if}{if count == "13"}first{/if}">\n				<a href="{title_permalink="site/article"}">\n					<img src="{home_feature_image}" alt="" height="376" width="376" />\n					<h3>{title}</h3>\n				</a>\n				<div class="cushion">\n					<p class="meta">\n						{entry_date format="%n.%d.%y"} | <em>By {author}</em> |\n						<a class="ss-social ss-icon" href="https://www.facebook.com/dialog/feed&link={title_permalink=''site/article''}">Like</a>\n						<a class="ss-social ss-icon" href="https://twitter.com/intent/tweet?url={title_permalink="site/article"}&hashtags=theheadsofstate">twitter</a>\n					</p>\n					<p>{article_intro_text}&hellip;</p>\n					<p class="more"><a href="{title_permalink="site/article"}">Read the full article&hellip;</a></p>	\n				</div>\n			</article>\n			{if count == ''3''}</div><!-- split3-->{/if}\n			{if count == ''6''}</div><!-- split3-->{/if}\n			{if count == ''9''}</div><!-- split3-->{/if}\n			{if count == ''12''}</div><!-- split3-->{/if}\n			{if count == ''15''}</div><!-- split3-->{/if}\n			{/exp:channel:entries}\n		</div>\n\n\n		<h2 class="move">Instagram</h2>\n		<div class="royalSlider rsDefault royalSlider2 insta-panel">\n\n			<div class="split3 handheld-horz rsContent">\n			\n				{exp:tgl_instagram:user_feed username="theheadsofstate" limit="30" cache="30"}\n\n				{if count==4 || count==7 || count==10 || count==13 || count==16 || count==19 || count==22 || count==25 || count==28 || count==31}\n				<div class="split3 handheld-horz">\n				{/if}	\n\n					<article class="col block-instagram {if count==1 ||count==4 || count==7 || count==10 || count==13 || count==16 || count==19 || count==22 || count==25 || count==28}first{/if}">\n						<a target="_blank" href="{link}">\n							<img src="{standard_resolution_url}" height="376" width="376" alt="">\n							<p>\n								<span class="likes">{likes_count} Likes</span>\n								<em>{exp:wb_relative_date}{created_at}{/exp:wb_relative_date}</em>\n							</p>\n						</a>\n					</article>\n\n				{if count==3 || count ==6 || count==9 || count==12 || count==15 || count==18 || count==21 || count==24 || count==27 || count==30}\n				</div>\n				{/if}\n\n				{/exp:tgl_instagram:user_feed}					\n\n\n		</div><!-- royalSlider-->\n\n	</div><!-- /wrap-->\n\n</section><!-- /social-footer-->\n\n\n');
