INSERT INTO `exp_templates` VALUES(7, 1, 6, 'head', 'y', 'webpage', '<!DOCTYPE html>\n<!--[if lt IE 7]>      <html class="no-js lt-ie9 lt-ie8 lt-ie7"> <![endif]-->\n<!--[if IE 7]>         <html class="no-js lt-ie9 lt-ie8"> <![endif]-->\n<!--[if IE 8]>         <html class="no-js lt-ie9"> <![endif]-->\n<!--[if gt IE 8]><!--> <html class="no-js"> <!--<![endif]-->\n<head>\n\n	<title>The Heads of State</title>\n\n	{embed="inc/meta"}\n	<script src="/a/j/vendor/modernizr-2.6.2-respond-1.1.0.min.js"></script>\n	{embed="inc/cssReference"}\n\n	{!--{if segment_1 == ""}\n		{exp:channel:entries channel="home_curtain" limit="1"}\n		<style>\n			.curtains>li#section1{\n			    background: #fff url({home_curtain_large}) 50% 120px no-repeat;\n			    background-size: contain;\n			}\n		</style>\n		{/exp:channel:entries}\n	{/if}--}\n\n\n\n<!--\n <style>\n#dimensions { position: fixed; background: rgba(0,0,0,0.5); color: #fff; top: 0; left: 0; z-index: 100000; font-size: 20px; padding: 2px; }\n  </style>\n<script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>\n	\n<script>\n $(document).ready(function() {\n$(''body'').prepend(''<p id="dimensions">Dimensions</p>'');\n });\n\nwindow.onresize = displayWindowSize;\nwindow.onload = displayWindowSize;\nfunction displayWindowSize() {\n    var windowWidth = $(window).width(); //retrieve current window width\n    var windowHeight = $(window).height(); //retrieve current window height\n    document.getElementById("dimensions").innerHTML = windowWidth + "x" + windowHeight;\n};\n\n</script>\n-->\n</head>\n', '', 1371093546, 1, 'n', 0, '', 'n', 'n', 'o', 0);
INSERT INTO `exp_templates` VALUES(8, 1, 6, 'header', 'y', 'webpage', '<header>\n	<div class="inner-wrap">\n		<div class="wrap">\n\n			{if segment_1 == ""}<h1 class="logo">The Heads <span>of</span> State</h1>\n			{if:else}\n			<strong ><a class="logo" href="/">The Heads <span>of</span> State</a></strong>{/if}\n\n			<nav class="main-nav"  role="navigation">{if segment_3 == "work"}class="on" {/if}\n				<ul>\n					<li><a {if segment_2 == "work"}class="on" {/if} href="/site/work/">Work</a></li>\n					<li><a {if segment_2 == "about"}class="on" {/if} href="/site/about/">About</a></li>\n					<li><a {if segment_2 == "case-studies"}class="on" {/if} href="/site/case-studies/">Case Studies</a></li>\n					<li><a target="_blank" href="http://store.theheadsofstate.com">Store</a></li>\n					<li><a {if segment_2 == "blog"}class="on" {/if} href="/site/blog/">Blog</a></li>\n					<li class="contact-trigger"><a href="#contact">Contact</a></li>\n				</ul>\n				{if segment_2 != "results"}\n				<a class="ss-icon ss-standard" href="#" id="search-modal-button">search</a>\n				{/if}\n			</nav>\n		</div><!-- /wrap-->\n	</div><!-- /inner-wrap-->\n</header>\n', '', 1372243871, 1, 'n', 0, '', 'n', 'n', 'o', 0);
INSERT INTO `exp_templates` VALUES(9, 1, 6, 'social-footer', 'y', 'webpage', '<section class="social-footer cards">\n\n	<span class="prev pager ss-icon ss-standard ss-navigaterright">Previous</span>\n	<span class="next pager ss-icon ss-standard ss-navigaterleft">Next</span>\n\n	<div class="wrap">\n\n		<div class="adjunct-header">\n			<ul>\n				<li><a class="on blog-trigger" href="#">Blog</a></li>\n				<li><a class="insta-trigger" href="#">Instagram</a></li>\n			</ul>\n			<p class="adjunct-link"><a href="/site/blog/">See All Posts</a></p>\n		</div>\n\n\n		<h2 class="move">Blog</h2>\n		<div class="royalSlider rsDefault blog-panel">\n			{exp:channel:entries channel="blog" dynamic="no" limit="15"}\n			{if count == ''1''}<div class="split3 rsContent">{/if}\n			{if count == ''4''}<div class="split3  rsContent">{/if}\n			{if count == ''7''}<div class="split3  rsContent">{/if}\n			{if count == ''10''}<div class="split3 rsContent">{/if}\n			{if count == ''13''}<div class="split3  rsContent">{/if}\n\n			<article class="col {if count == "1"}first{/if}{if count == "4"}first{/if}{if count == "7"}first{/if}{if count == "10"}first{/if}{if count == "13"}first{/if}">\n				<a href="{title_permalink="site/article"}">\n					<img src="{home_feature_image}" alt="" height="376" width="376" />\n					<h3>{title}</h3>\n				</a>\n				<div class="cushion">\n					<p class="meta">\n						{entry_date format="%n.%d.%y"} | <em>By {author}</em> <span>|</span>\n						<a class="ss-social ss-icon" href="https://www.facebook.com/dialog/feed&link={title_permalink=''site/article''}">Like</a>\n						<a class="ss-social ss-icon" href="https://twitter.com/intent/tweet?url={title_permalink="site/article"}&hashtags=theheadsofstate">twitter</a>\n					</p>\n					<p class="article">{article_intro_text}&hellip;</p>\n					<p class="more"><a href="{title_permalink="site/article"}">Read the full article&hellip;</a></p>	\n				</div>\n			</article>\n			{if count == ''3''}</div><!-- split3-->{/if}\n			{if count == ''6''}</div><!-- split3-->{/if}\n			{if count == ''9''}</div><!-- split3-->{/if}\n			{if count == ''12''}</div><!-- split3-->{/if}\n			{if count == ''15''}</div><!-- split3-->{/if}\n			{/exp:channel:entries}\n		</div>\n\n\n		<h2 class="move">Instagram</h2>\n		<div class="royalSlider rsDefault royalSlider2 insta-panel">\n\n			<div class="split3 rsContent">\n			\n				{exp:tgl_instagram:user_feed username="theheadsofstate" limit="30" cache="30"}\n\n				{if count==4 || count==7 || count==10 || count==13 || count==16 || count==19 || count==22 || count==25 || count==28 || count==31}\n				<div class="split3 handheld-horz">\n				{/if}	\n\n					<article class="col block-instagram {if count==1 ||count==4 || count==7 || count==10 || count==13 || count==16 || count==19 || count==22 || count==25 || count==28}first{/if}">\n						<a target="_blank" href="{link}">\n							<img src="{standard_resolution_url}" height="376" width="376" alt="">\n							<p>\n								<span class="likes">{likes_count} Likes</span>\n								<em>{exp:wb_relative_date}{created_at}{/exp:wb_relative_date}</em>\n							</p>\n						</a>\n					</article>\n\n				{if count==3 || count ==6 || count==9 || count==12 || count==15 || count==18 || count==21 || count==24 || count==27 || count==30}\n				</div>\n				{/if}\n\n				{/exp:tgl_instagram:user_feed}					\n\n\n		</div><!-- royalSlider-->\n\n	</div><!-- /wrap-->\n\n</section><!-- /social-footer-->\n\n', '', 1372244724, 1, 'n', 0, '', 'n', 'n', 'o', 0);
INSERT INTO `exp_templates` VALUES(10, 1, 6, 'footer', 'y', 'webpage', '<footer id="contact">\n	<div class="wrap">\n\n		<div class="col-1">\n			<h2>Contact Us</h2>\n\n			<div class="split2">\n\n				<div class="col first">\n\n					<p class="address">\n						<strong>The Heads of State</strong>\n						<a target="_blank"  title="Go to Google Maps" href="https://maps.google.com/maps?q=39+N.+3rd+Street+%7C+2nd+Floor&amp;ie=UTF-8&amp;hq=&amp;hnear=0x89c6c885cdfed2f9:0x7f7f7b0de6fc04cb,39+N+3rd+St,+Philadelphia,+PA+19106&amp;gl=us&amp;daddr=39+N+3rd+St,+Philadelphia,+PA+19106&amp;ei=ZKWmUcLyDtjI4APrlYHAAQ&amp;ved=0CDIQwwUwAA">39 N. 3rd Street | 2nd Floor<br />\n						Philadelphia, PA 19106</a>\n					</p>\n\n				</div>\n\n				<div class="col social-header">\n\n					<h3>Follow Us</h3>\n					<p>\n						<a class="f-fb ss-social ss-icon" target="_blank" href="https://www.facebook.com/theheadsofstate">Facebook</a>\n						<a class="f-twitter ss-social ss-icon" target="_blank"  href="https://twitter.com/theheadsofstate">Twitter</a>\n						<a class="f-instagram ss-social ss-icon" target="_blank"  href="http://instagram.com/theheadsofstate/">Instagram</a>\n						<a class="f-pintrest ss-social ss-icon" target="_blank"  href="http://pinterest.com/theheadsofstate/">Pinterest</a>\n					</p>\n\n				</div><!--col-->\n\n			</div><!-- split-2-alt -->\n\n			<p class="f-email ss-standard ss-mail"><a href="mailto:studio@theheadsofstate.com">studio@theheadsofstate.com</a></p>\n\n			<form class="mailing-list" action="http://theheadsofstate.createsend.com/t/y/s/wjjui/" method="post">\n				<label class="move">Join Our Mailing List</label>\n				<input type="text" name="cm-wjjui-wjjui" id="wjjui-wjjui" placeholder="Join our mailing list">\n				<input type="submit" class="btn" value="Sign Up">\n			</form>\n\n		</div><!-- col-->\n\n		<div class="col-2">\n\n			<h2>Questions?</h2>\n\n			{exp:freeform:form \n				form:class="split2"\n				form_name="contact"\n				return="site/contact-confirmation" \n			}\n				<div class="col first">\n					<input type="text" placeholder="Name" name="name"/>\n					<input type="email" placeholder="Email" name="email"/>\n					<input type="tel" placeholder="Phone" name="phone"/>\n					<input type="text" placeholder="Company" name="company"/>\n				</div>\n				<div class="col">\n					<select class="styled-select" style="width: 100%" name="business">\n						<option>How May We Direct Your Call?</option>\n						<option value="New Business">New Business</option>\n						<option value="Old Business">Old Business</option>\n						<option value="Love Letters">Love Letters</option>\n						<option value="Press and Speaking">Press &amp; Speaking</option>\n						<option value="General Questions">General Questions</option>\n					</select>\n\n					<textarea name="message"></textarea>\n\n					<input type="submit" class="btn" value="Submit">\n				</div>\n			{/exp:freeform:form}\n		</div><!-- col -->\n\n\n\n		<p class="copyright">&copy; The Heads of State 2013. All rights Reserved.</p>\n\n	</div>\n</footer>', '', 1371468492, 1, 'n', 0, '', 'n', 'n', 'o', 0);
INSERT INTO `exp_templates` VALUES(11, 1, 6, 'jsReference', 'y', 'webpage', '\n		<script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>\n		<script src="/public/a/j/plugins.js"></script>\n		<script src="/public/a/j/vendor/jquery.royalslider.custom.min.js"></script>\n		<script src="/public/a/j/vendor/jquery.timeago.js"></script>\n		<script src="/public/a/j/main.min.js"></script>\n		<script src="/public/a/j/vendor/stickystack.js"></script>\n		<script src="/public/a/j/vendor/jquery.ddslick.min.js"></script>\n		<script src="/public/a/j/vendor/waypoints.min.js"></script>\n	\n\n		<script>\n		    jQuery(document).ready(function($) {\n		        $(".royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	loop: true,\n		        	arrowsNav: false,\n		        	autoHeight:true\n\n		        }); \n		        $(".royalSlider2").royalSlider({\n    				controlNavigation: ''none'',\n    				arrowsNav: false,\n    				autoHeight:true,\n    				loop: true\n\n    			});  	\n		        $(".cs-royalSlider").royalSlider({\n		        	controlNavigation: ''none'',\n		        	arrowsNav: false,\n		        	autoScaleSlider:true,\n		        	loop: true, \n		        	autoScaleSliderWidth: ''1190'', \n		        	autoScaleSliderHeight: ''506'',\n		        	imageScalePadding: 0,\n		        	slideSpacing: 0,\n		        	transitionType: ''fade'',\n		        	autoPlay: {\n		        		// autoplay options go gere\n		        		enabled: true,\n		        		delay: 4000\n		        	}\n		        }); \n	  		 var slider = $(".royalSlider").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider.prev();\n				})\n				$(''.next'').click(function(){\n					slider.next();\n\n				});\n			var slider2 = $(".royalSlider2").data(''royalSlider'');\n				$(''.prev'').click(function(){\n					slider2.prev();\n				})\n				$(''.next'').click(function(){\n					slider2.next();\n\n				});	\n			// var slider3 = $(".cs-royalSlider").data(''royalSlider'');\n			// 	$(''.prev-cs'').click(function(){\n			// 		slider3.prev();\n			// 	})\n			// 	$(''.next-cs'').click(function(){\n			// 		slider3.next();\n\n			// 	});					\n			});\n\n			$(''.styled-select'').ddslick();\n		</script>\n		<script>\n\n			$container = $(".header-inner-wrapper")\n			$totalHeight = $container.outerHeight();\n\n			$(''body:not(.home) .search-wrap'')\n			  .waypoint(function(direction) {\n			    if (direction === ''down'') { \n			    	$(".hover-trigger").fadeIn("fast");\n			    	$container.removeClass("top").addClass("fixed").css("top", -$totalHeight);\n			    	$("body").css("padding-top", $totalHeight);\n			    }\n			  }, { offset: 20 })\n			  .waypoint(function(direction) {\n			    if (direction === ''up'') { \n			    	$(".hover-trigger").fadeOut("fast");\n			    	$container.removeClass("fixed").css("top", 0);\n			    	$("body").css("padding-top", 0);\n			    	$(".hover-trigger span").fadeIn();\n			    }\n			  }, { offset: 20 });\n\n\n			  $(''.home .home-ps'')\n			    .waypoint(function(direction) {\n			      if (direction === ''down'') { \n			      	$(".hover-trigger").fadeIn("fast");\n			      	$container.removeClass("top").addClass("fixed").css("top", -$totalHeight);\n			      	$("body").css("padding-top", $totalHeight);\n			      }\n			    }, { offset: 20 })\n			    .waypoint(function(direction) {\n			      if (direction === ''up'') { \n			      	$(".hover-trigger").fadeOut("fast");\n			      	$container.removeClass("fixed").css("top", 0);\n			      	$("body").css("padding-top", 0);\n			      	$(".hover-trigger span").fadeIn();\n			      }\n			    }, { offset: 20 });\n	\n\n			$(''.hover-trigger'').hover(function() {\n				$(".hover-trigger span").fadeOut();\n				$(".header-inner-wrapper").addClass("expanded").animate({\n	  				top: 0\n				});\n\n			});\n\n			$container.mouseleave(function() {\n				$(this).removeClass("expanded").animate({\n					top:  -$totalHeight\n				});\n				$(".hover-trigger span").fadeIn();\n			});				\n\n			$container.waypoint(function() {\n				$(this).addClass("top")\n				console.log("add top class");\n			});	\n				\n	</script>\n\n	<script>\n		$(document).ready(function(){\n			// Show search modal\n			$(''#search-modal-button'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeIn(500);\n				$(''#search-modal-keywords'').focus();\n			});\n\n			// Close search modal\n			$(''#search-modal-close'').click(function(e){\n				e.preventDefault();\n				$(''#search-modal'').fadeOut(100);\n			});\n\n			// Catch the search modal submit event\n			// and do the searches via ajax\n			$(''#search-modal-form'').submit(function(e){\n				e.preventDefault();\n\n				// grab the keywords field\n				var keywords = $(''#search-modal-keywords'').val();\n				\n				// clear any existing results\n				$(''.search-results .wrap'').html('''');\n\n				// set the two hidden keywords field\n				// values to the user input\n				$(''#blog-keywords'').val(keywords);\n				$(''#work-keywords'').val(keywords);\n\n				// perform the ajax post for the blog search\n				var action = $(''#blog-search-form'').attr(''action'');\n				var form = $(''#blog-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n				// perform ajax post for the work search\n				var action = $(''#work-search-form'').attr(''action'');\n				var form = $(''#work-search-form'').serialize();\n\n				$.post(action, form, function(data){\n					$(''.search-results .wrap'').append(data);\n				});\n\n			});\n\n			// Load archives\n			$(''.see-more h2 a'').click(function(e){\n				e.preventDefault();\n				\n				var page = $(''nav.subnav ul li.on a'').data(''page'');\n\n				var category = $(''section#'' + page + '' input.category'').val();\n				var children = $(''section#'' + page + '' input.children'').val();\n				children.replace(''|'', ''&'');\n\n				$.get(''/inc/archives/'' + category + ''/'' + children, function(data) {\n  					$(''#archive-holder'').html(data);\n  				});\n				\n			});\n\n			$(document).on(''click'',''.subnav a'',function(e){\n 				if  ($(''nav.subnav ul li.on a'').data(''page'') == "branding" || $(''nav.subnav ul li.on a'').data(''page'') == "books" || $(''nav.subnav ul li.on a'').data(''page'') == "ads-collateral") {\n 					$(".see-more").hide();\n 				}\n 				else {\n 					$(".see-more").show();\n 				}\n			});\n\n		});\n\n			$(document).on(''click'',''.about-list .clients-head'',function(e){\n				$(".head-wrap h3").removeClass("on");\n 				$(this).addClass("on");\n 				$(".accolades, .speaking").hide();\n 				$(".about-list .clients").show();\n			});\n			$(document).on(''click'',''.about-list .accolades-head'',function(e){\n 				$(".head-wrap h3").removeClass("on");\n 				$(this).addClass("on");\n 				$(".about-list .clients, .about-list .speaking").fadeOut(function(){\n 					$(".about-list .accolades").fadeIn();\n 				});\n 				\n			});\n			$(document).on(''click'',''.about-list .speaking-head'',function(e){\n				$(".head-wrap h3").removeClass("on");\n 				$(this).addClass("on");\n 				$(".about-list .accolades, .about-list .clients").fadeOut(function(){\n 					$(".about-list .speaking").fadeIn();\n 				});\n			});\n	</script>', '', 1372244019, 1, 'n', 0, '', 'n', 'n', 'o', 0);
