INSERT INTO `exp_templates` VALUES(36, 1, 1, 'results', 'y', 'webpage', '	{embed="inc/head"}\n	<body class="search-results-page">\n		<!--[if lt IE 7]>\n			<p class="chromeframe">You are using an <strong>outdated</strong> browser. Please <a href="http://browsehappy.com/">upgrade your browser</a> or <a href="http://www.google.com/chromeframe/?redirect=true">activate Google Chrome Frame</a> to improve your experience.</p>\n		<![endif]-->\n		<style>\n			.search-results-page  .search-results { position: static; }\n			.search-drawer { \n				margin-top: -20px;\n				z-index: 1;\n			}\n		</style>\n		<div class="header-inner-wrapper">\n			{embed="inc/header"}\n		</div>\n		\n		<div class="search-drawer">\n			<div class="topper">\n				<div class="wrap">\n					<form id="search-results-form">\n						<input type="text" id="search-results-keywords" value="{exp:search:keywords}" />\n					</form>\n				</div>\n			</div>\n		</div><!-- /search-drawer-->\n\n\n		{exp:search:search_results}\n		{if count==1}\n		<div class="search-results">\n			<div class="wrap">				\n				<div class="adjunct-header">\n					<p class="results-count"><strong>{exp:search:total_results} results in <span>{channel_name}</span></strong></p>\n				</div>\n				<ul>\n		{/if}		\n					<li>\n						<a href="{auto_path}">\n							{if channel_short_name == "blog"}\n								<img src="{home_feature_image}" alt="" />\n							{if:elseif channel_short_name == "work"}\n								<img src="{archive_thumb}" />\n							{/if}\n							<p>									\n								<strong class="meta">Category | <em>{categories backspace="2"}{category_name}, {/categories}</em></strong>\n								<h3>{title}</h3>\n								<p>{excerpt}</p>\n							</p>\n						</a>\n					</li>\n				{if count == total_results}		\n				</ul>\n				\n\n				{paginate}\n				<div class="search-pagination">\n        			<p>Page {current_page} of {total_pages} pages {pagination_links}</p>\n				</div>\n				{/paginate}	\n\n			</div>\n		</div><!-- /search-results-->\n		{/if}\n		{/exp:search:search_results}	\n\n\n		{embed="inc/footer"}\n		{embed="inc/jsReference"}\n		<script>\n			$(document).ready(function(){\n				// Catch the search form submit event\n				// and do the searches via ajax\n				$(''#search-results-form'').submit(function(e){\n					e.preventDefault();\n\n					// grab the keywords field\n					var keywords = $(''#search-results-keywords'').val();\n					\n					// clear any existing results\n					$(''.search-results .wrap'').html('''');\n\n					// set the two hidden keywords field\n					// values to the user input\n					$(''#blog-keywords'').val(keywords);\n					$(''#work-keywords'').val(keywords);\n\n					// perform the ajax post for the blog search\n					var action = $(''#blog-search-form'').attr(''action'');\n					var form = $(''#blog-search-form'').serialize();\n\n					$.post(action, form, function(data){\n						$(''.search-results .wrap'').append(data);\n					});\n\n					// perform ajax post for the work search\n					var action = $(''#work-search-form'').attr(''action'');\n					var form = $(''#work-search-form'').serialize();\n\n					$.post(action, form, function(data){\n						$(''.search-results .wrap'').append(data);\n					});\n\n				});\n			});\n		</script>\n		{!-- these are the search forms created by ee. we serialize them with jquery and submit with ajax --}\n		<div id="search-forms" style="display:none;">\n		{exp:search:simple_form \n			channel="blog" \n			form_id="blog-search-form"\n			result_page="inc/search-results-ajax-blog"\n			no_result_page="inc/search-no-results-ajax/blog"\n			results="10"\n			search_in="everywhere"\n		}\n		<input type="hidden" id="blog-keywords" name="keywords" value="" >  \n		{/exp:search:simple_form}\n\n		{exp:search:simple_form \n			channel="work" \n			form_id="work-search-form"\n			result_page="inc/search-results-ajax-work"\n			no_result_page="inc/search-no-results-ajax/work"\n			results="10"\n			search_in="everywhere"\n		}\n		<input type="hidden" id="work-keywords" name="keywords" value="" >  \n		{/exp:search:simple_form}\n		</div>\n	</body>\n</html>', '', 1372243703, 1, 'n', 0, '', 'n', 'n', 'o', 23);
INSERT INTO `exp_templates` VALUES(37, 1, 6, 'search-modal', 'y', 'webpage', '<div id="search-modal">\n\n	<div class="search-drawer">\n		<div class="topper">\n			<div class="wrap">\n				<form id="search-modal-form">\n					<input type="text" id="search-modal-keywords" placeholder="Keywords.." />\n				</form>\n\n				<a class="close" href="#" id="search-modal-close">x</a>\n			</div>\n		</div>\n	</div><!-- /search-drawer-->\n\n	<div class="search-results">\n		<div class="wrap">\n			{!-- search results get injected here --}\n		</div>\n	</div><!-- /search-results-->\n\n	{!-- these are the search forms created by ee. we serialize them with jquery and submit with ajax --}\n	<div id="search-forms" style="display:none;">\n		{exp:search:simple_form \n			channel="blog" \n			form_id="blog-search-form"\n			result_page="inc/search-results-ajax-blog"\n			no_result_page="inc/search-no-results-ajax/blog"\n			results="20"\n			search_in="everywhere"\n		}\n		<input type="hidden" id="blog-keywords" name="keywords" value="" >  \n		{/exp:search:simple_form}\n\n		{exp:search:simple_form \n			channel="work" \n			form_id="work-search-form"\n			result_page="inc/search-results-ajax-work"\n			no_result_page="inc/search-no-results-ajax/work"\n			results="20"\n			search_in="everywhere"\n		}\n		<input type="hidden" id="work-keywords" name="keywords" value="" >  \n		{/exp:search:simple_form}\n	</div>\n</div>\n', '', 1372243679, 1, 'n', 0, '', 'n', 'n', 'o', 0);
INSERT INTO `exp_templates` VALUES(38, 1, 6, 'search-results-ajax-blog', 'y', 'webpage', '{exp:search:search_results}\n{if count==1}\n<div class="adjunct-header">\n	<p class="results-count"><strong>{total_results} Result{if total_results > 1}s{/if} in <span>blog</span></strong></p>\n	{if total_results>5}<p class="adjunct-link"><a href="/site/results/{segment_3}">See All Results</a></p>{/if}\n</div>\n\n<ul>\n{/if}\n{if count<5}	\n	<li>\n		<a href="{auto_path}">\n			<img src="{home_feature_image}" alt="" />\n			<div>\n				<p class="meta">Category | <em>{categories backspace="2"}{category_name}, {/categories}</em</p>\n				<h3>{title}</h3>\n				<p>{excerpt}</p>\n			</div>\n		</a>\n	</li>\n{/if}\n{if count == 5 || count == total_results}	\n</ul>\n\n{/if}\n{if count > 5 && count == total_results}	\n<p><a href="/site/results/{segment_3}">See All Results From the Blog</a></p>\n{/if}\n{/exp:search:search_results}', '', 1372243804, 1, 'n', 0, '', 'n', 'n', 'o', 79);
INSERT INTO `exp_templates` VALUES(39, 1, 6, 'search-results-ajax-work', 'y', 'webpage', '{exp:search:search_results}\n{if count==1}\n<div class="adjunct-header">\n	<p class="results-count"><strong>{total_results} Result{if total_results > 1}s{/if} in <span>work</span></strong></p>\n	{if total_results > 5}<!--<p class="adjunct-link"><a href="/site/results/{segment_3}">See All Results</a></p>-->{/if}\n</div>\n\n<ul>\n{/if}\n{if count<5}	\n	<li>\n		<a href="{auto_path}">\n\n			<img src="{archive_thumb}" alt="" />\n			<div>\n				<p class="meta">Category | <em>{categories backspace="2"}{category_name}, {/categories}</em</p>\n				<h3>{title}</h3>\n				<p>{excerpt}</p>\n			</div>\n		</a>\n	</li>\n{/if}\n{if count == 5 || count == total_results}	\n</ul>\n\n{/if}\n{if count > 5 && count == total_results}	\n<p><a href="/site/results/{segment_3}">There&rsquo;s more where that came from...</a></p>\n{/if}\n\n\n{/exp:search:search_results}', '', 1372243792, 1, 'n', 0, '', 'n', 'n', 'o', 81);
INSERT INTO `exp_templates` VALUES(40, 1, 6, 'search-no-results-ajax', 'y', 'webpage', '<div class="adjunct-header no-results">\n	{if segment_3 =="blog"}\n	<p class="results-count"><strong>Sorry Charlie, we couldn&rsquo;t find anything for you in the <a href="/site/blog/">Blog</a></strong>.</p>\n	{if:else}\n	<p class="results-count"><strong>Womp womp. No results from our<a href="/site/work/">Work</a> this time</strong>.</p>\n\n	{/if}\n</div>', '', 1372243855, 1, 'n', 0, '', 'n', 'n', 'o', 62);
