INSERT INTO `exp_templates` VALUES(54, 1, 1, 'about', 'y', 'webpage', '	{embed="inc/head"}\n	<body class="about">\n		<!--[if lt IE 7]>\n			<p class="chromeframe">You are using an <strong>outdated</strong> browser. Please <a href="http://browsehappy.com/">upgrade your browser</a> or <a href="http://www.google.com/chromeframe/?redirect=true">activate Google Chrome Frame</a> to improve your experience.</p>\n		<![endif]-->\n		<div class="header-inner-wrapper">\n			{embed="inc/header"}\n		</div>\n		{embed="inc/search-modal"}\n		<div class="hover-trigger"><span>Menu</span></div>\n	\n		{exp:channel:entries channel="about"}\n		\n		<div class="search-wrap content">\n			\n			<section class="hero">\n				<div class="wrap">\n					<h2 class="positioning-statement">{tagline}</h2>\n					<p class="subhead-alt">{intro_text}</p>\n				</div>\n			</section>\n\n			<div class="wrap">\n\n				<div class="carousel-wrap">\n					<div class="carousel cs-royalSlider  rsDefault">\n						{slideshow_images}\n						<img src="{image}" alt="{caption}" />\n						{/slideshow_images}\n					</div>\n					<!--<span class="prev-cs  image-pager ss-icon ss-standard ss-navigaterright">Previous</span>\n					<span class="next-cs image-pager ss-icon ss-standard ss-navigaterleft">Next</span>-->\n				</div>\n\n								<section class="split3_2-1-alt cards">\n					<article class="col first about-us">\n						<h2>About Us</h2>\n						<p class="intro">{intro_blurb}</p> \n						<p>{about_body}</p> \n\n						<h2>History</h2>\n						<p class="meta">\n							Est | <em>2002</em>\n						</p>\n						<p>{history_text}</p>\n					</article>\n\n					<div class="col">\n						\n						<section class="about-list">\n							<div class="head-wrap">\n								<h3 class="clients-head on">Clients</h3>\n								<h3 class="accolades-head">Awards</h3>\n								<h3 class="speaking-head">Speaking</h3>\n							</div>\n								\n								<ul class="clients tab-list">\n									{client_list}\n									<li>{client_name}</li>\n									{/client_list}\n								</ul>\n\n\n								\n							<ul class=" accolades tab-list">\n								{selected_accolades}\n								<li>{accolade}</li>\n								{/selected_accolades}\n\n							</ul>\n\n							<div class="speaking tab-list">\n								\n								<ul>\n									{public_speaking}\n									<li>{speaking_item}</li>\n									{/public_speaking}\n								</ul>\n\n								<p>We speak regularly at conferences, universities, and international and regional events. To inquire about our availability for your conference or event, please get in touch with us.</p>\n							</div>\n						</section><!-- split3-->\n\n					</div>\n				</section><!-- /split3_1-2-->\n\n				<section class="split3 cards">\n					{personel}\n					<article class="col {if row_count == "1"}first{/if}">\n						<img src="{image}">\n						<h3>{name}</h3>\n						<div class="cushion">\n							<p class="meta">{title}</p>\n							<p>{bio}</p>\n						</div>\n					</article>\n					{/personel}\n				</section><!-- split3-->\n\n\n\n\n			</div><!-- /wrap-->\n		</div><!-- / search-wrap-->\n		{/exp:channel:entries}\n\n		{embed="inc/footer"}\n		{embed="inc/jsReference"}\n\n	\n	</body>\n</html>', '', 1372241149, 1, 'n', 0, '', 'n', 'n', 'o', 129);
